<div class="hidden-sm hidden-md hidden-lg" id="smallMenuDiv">            <div class="dropdown">                <button onclick="toggleMenu()" class="dropbtn"><img src='frontend/design/images/menu.png' height='80px'></button>					                </div>                                <script>					var smallMenuVisible = 0;	                function toggleMenu() {	                	var target = $("#largeMenuWrapper");	                	var targetHeight = $("#mainPage").height();	                	if(target.css("display") == "none"){	                		smallMenuVisible = 1;	                	}	                	else{	                		smallMenuVisible = 0;		                	}	                	target.css("height",targetHeight);	                    target.toggle("slide");	                }	                function clickMain(){	                	if(smallMenuVisible == 1){	                		toggleMenu();	                	}	                }       	            $( document ).ready(function() {        	                $("#mainPage").click(function(){	                	clickMain();	                });                });                </script>			        </div><div id="characterDivSmall">        <?php                 	#season finals        	if (isset($_SESSION['final'])){    			if(isset($_SESSION['loggedIn'])){	        		echo "<a class='outButtons' id='logoutButton' href='#'>Logout</a>";				}				else{					echo "<a class='outButtons' href='index.php?page=login'>Login</a>";					echo "<a class='outButtons' href='index.php?page=register'>Register</a>";				}				echo "<br>";				echo "<a href='index.php?page=seasonFinals'><h2 style='text-align:center'>Season " . $_SESSION['final'] . " Finals!</h2></a>";        	}			else{	        	if(isset($_SESSION['loggedIn'])){	        		if(isset($_GET['page'])){	        			if($_GET['page'] == "logout"){	        				echo "<a class='outButtons' href='index.php?page=login'>Login</a>";